m = 501
total = 0

#external file
file = open("Text.txt", "r")
contents = file.read()
file.close()
array = contents.split(" ")
array = [eval(i) for i in array]

for i in range(m):
    for j in range(len(array)):
        if array[j] % 2 == 0: #even
            array[j] = (array[j]/2)
        elif array[j] % 2 == 1: #odd
            array[j] = ((3*array[j])+1)

for i in range(len(array)):
    total = total + array[i]

print(total)


